/* code to fix reativar versão on google-chrome etc... */    
function IdentificarProcesso(Controle) {
    if (Controle.className.indexOf("-1_-1") == -1) {
        showFloatingPanel(prefixo+'Results');
	    var fpBody=document.getElementById(prefixo+'Results_BodyRow');
	    if (fpBody.style.display=="none")
	        toggleFloatingPanelState(prefixo+'Results','../../Imagens/collapse.gif','../../Imagens/expand.gif');
        var message = "ControlID="+prefixo+"Map1&ControlType=Map&EventArg=MapIdentifyProcess&CodigoIDEvento=" + Controle.className.replace('/','_');
        var context = "label";
        WebForm_DoCallback('__Page',message,processCallbackResult,context,postBackError,true);
    } else {		
        var message = "ControlID="+prefixo+"Map1&ControlType=Map&EventArg=MapChangeHistoricalVersion&CodigoIDEvento=" + Controle.className;
        var context = "label";
        WebForm_DoCallback('__Page',message,processCallbackResult,context,postBackError,true);
        window.open('MemorialDescritivo.aspx?Versao=' + Controle.className.split('_')[0], 'popup_window', 'width=700,height=570,left=100,top=100,resizable=no,scrollbars=yes');
    }
}
